<?phpsession_start();require_once 'autoload.php';use Facebook\FacebookSession;//require_once dirname(__FILE__) . '/DB.class.php';class FBLogin {	private $_db;		public function __construct() {		$this->_db = DB::getInstance();	}		public function saveDetails( $details ) {		$result = $this->_db->query( "INSERT INTO fb_users (user_fb_uid, user_firstname, user_lastname, user_email) VALUES		('".$details['id']."', '".$details['first_name']."', '".$details['last_name']."', '".$details['email']."')" );		if ( !$result )			return $this->_db->error();	}	public function matchUser( $access_token ) {		FacebookSession::setDefaultApplication( '1594113490874544', 'fca50280932a6065e68a540ac3f2925b' );		$session = new FacebookSession( $access_token );		try {			$session->validate();		} catch ( FacebookRequestException $ex ) {			return false;		} catch ( \Exception $ex ) {			return false;		}		return true;	}}